var pageHeader=document.querySelector(".page-header"),navLogo=document.querySelector(".main-nav__logo"),navMenu=document.querySelector(".main-nav__menu");window.addEventListener("scroll",(function(){window.pageYOffset>=pageHeader.offsetHeight-2?(pageHeader.classList.add("page-header--scroll"),navLogo.classList.add("main-nav__logo--dark")):(pageHeader.classList.remove("page-header--scroll"),navMenu.classList.contains("main-nav__menu--closed")&&navLogo.classList.remove("main-nav__logo--dark"))}));